import React, { useEffect, useState } from 'react';
import { fetchFlightStatus, updateFlightStatus, sendNotification } from './Services/api';
import { requestFCMToken, listenForMessages } from './Services/firebase';
import axios from 'axios';
function App() {
    const [flights, setFlights] = useState([]);
    const [flightId, setFlightId] = useState('');
    const [status, setStatus] = useState('');


    useEffect(() => {
        const getToken = async () => {
            const token = await requestFCMToken();
            if (token) {
                console.log("FCM Token:", token);
            }
        };

        getToken();

        listenForMessages((payload) => {
            console.log("Message received: ", payload);
        });

        const fetchData = async () => {
            const flightData = await fetchFlightStatus();
            setFlights(flightData);
        };

        fetchData();
    }, []);

    const fetchFlightStatus = async () => {
        try {
            const response = await axios.get('http://localhost:5000/get_flight_status');
            setFlights(response.data);
        } catch (error) {
            console.error("Error fetching flight status:", error);
        }
    };

    const handleStatusUpdate = async () => {
        if (flightId && status) {
            try {
                await axios.post('http://localhost:5000/update_flight_status', {
                    Flightid: flightId,
                    Status: status,
                });
                alert('Flight status updated successfully!');
                // Optionally, refresh flight status
                fetchFlightStatus();
            } catch (error) {
                console.error("Error updating flight status:", error);
            }
        } else {
            alert('Please select a flight and a status.');
        }
    };
    useEffect(() => {
        fetchFlightStatus();
    }, []);

    return (
        <div>
            <h1>Flight Status App</h1>
            <select value={flightId} onChange={e => setFlightId(e.target.value)}>
                <option value="">Select Flight</option>
                {flights.map(flight => (
                    <option key={flight.FlightID} value={flight.FlightID}>
                        {flight.FlightNumber} - {flight.Status}
                    </option>
                ))}
            </select>
            <select value={status} onChange={e => setStatus(e.target.value)}>
                <option value="">Select Status</option>
                <option value="On Time">On Time</option>
                <option value="Delayed">Delayed</option>
                <option value="Cancelled">Cancelled</option>
            </select>
            <button onClick={handleUpdateFlightStatus}>Update Flight Status</button>
        </div>
    );
};

export default FlightStatusApp;
